text.select:
if(starts-with((parent::*/@css:top, for $i in parent::*/@role return /dbk:hub/dbk:info/css:rules/css:rule[$i eq @name]/@css:top)[1], '-'))
                    then concat('\raisebox{', replace((parent::*/@css:top, for $i in parent::*/@role return /dbk:hub/dbk:info/css:rules/css:rule[$i eq @name]/@css:top)[1], '^-', ''), '}')
                    else concat('\raisebox{-', (parent::*/@css:top, for $i in parent::*/@role return /dbk:hub/dbk:info/css:rules/css:rule[$i eq @name]/@css:top)[1], '}')

concat(if(local-name() eq 'subscript'
                           or exists(for $i in ./@role 
                                     return /dbk:hub/dbk:info/css:rules/css:rule[$i eq @name]
                                                                                [@remap = ('subscript')])
                                     )
                           then '_'
                           else '^',
                           '{',
                           if(not(@css:font-style eq 'italic') and not(@css:font-weight eq 'bold') and matches(., '^[-\+−\d\s{}]+$'))
                             then ''
                           else if(not(@css:font-style eq 'italic') and not(@css:font-weight eq 'bold')) 
                             then '\mathrm{'
                           else if(@css:font-weight eq 'bold' and not(@css:font-style eq 'italic')) 
                             then '\mathbf{'
                           else if(@css:font-weight eq 'bold') 
                             then '\mathbfit{'
                           else '')

concat('}', 
                          if(not(@css:font-style eq 'italic') and not(@css:font-weight eq 'bold') and matches(., '^[-\+−\d\s{}]+$')) 
                            then ''
                          else if(not(@css:font-style eq 'italic') or @css:font-weight eq 'bold') 
                            then '}' 
                          else   ''  )

if(//dbk:para[@role = ('Index_Heading', 'Indexberschrift')][string-length() gt 0]) 
                    then concat('\renewcommand{\indexname}{', 
                                //dbk:para[@role = ('Index_Heading', 'Indexberschrift')][string-length() gt 0][1], 
                                '}
\printindex')
                    else '\printindex'

xsl:value-of.select:
concat('@', 
                                 if(matches($main-set/@relation, 'thesis')) 
                                   then 'phdthesis'
                                 else if(matches($main-set/@relation, 'broadcast')) 
                                   then 'misc'
                                 else if($main-set/@relation) then $main-set/@relation
                                 else 'misc', 
                                 '{')

xsl:variable.select:
if(string-length($override) gt 0 and @numeration) 
                          then tr:list-number-to-integer($override, @numeration) - 1
                          else 0
