xsl:sequence.select:
if ($a11y) 
                          then exists($elt[parent::*[self::*:legalnotice] 
                                           or 
                                           (every $node in node() satisfies $node[self::processing-instruction()])
                                          ]
                                     ) 
                          else true()

xsl:value-of.select:
if (matches($line-style,'dotted'))
                          then '\dotuline{'
                          else if (matches($line-style,'dashed'))
                               then '\dashuline{'
                               else if (matches($line-style,'double'))
                                     then  '\uuline{'
                                     else  '\uline{'

text.select:
if(starts-with((parent::*/@css:top, key('style', parent::*/@role)/@css:top)[1], '-'))
                    then concat('\raisebox{', replace((parent::*/@css:top, key('style', parent::*/@role)/@css:top)[1], '^-', ''), '}')
                    else concat('\raisebox{-', (parent::*/@css:top, key('style', parent::*/@role)/@css:top)[1], '}')

xsl:value-of.select:
for $color-code in (@css:color, key('style', parent::*/@role)/@css:color)[1] 
                          return if(exists(tr:color-hex-rgb-to-keyword($color-code)))
                                 then tr:color-hex-rgb-to-keyword($color-code)[1]
                                 else concat('color-', upper-case(substring-after($color-code, '#')))

param.select:
for $color-code in (@css:background-color, key('style', @role)/@css:background-color)[1] 
                     return if(exists(tr:color-hex-rgb-to-keyword($color-code)))
                            then tr:color-hex-rgb-to-keyword($color-code)[1]
                            else concat('color-', upper-case(substring-after($color-code, '#')))

xsl:variable.select:
count(
                                for $i in (1 to $pos) 
                                return $footnotes[$i][not(.//dbk:phrase[@role eq 'hub:identifier'][1][@xreflabel])]
                                )

text.select:
if(//dbk:para[@role = ('Index_Heading', 'Indexberschrift')][string-length() gt 0]) 
                    then concat('\renewcommand{\indexname}{', 
                                //dbk:para[@role = ('Index_Heading', 'Indexberschrift')][string-length() gt 0][1], 
                                '}
\printindex')
                    else '\printindex'

xsl:value-of.select:
concat('@', 
                                 if(matches($main-set/@relation, 'thesis')) 
                                   then 'phdthesis'
                                 else if(matches($main-set/@relation, 'broadcast')) 
                                   then 'misc'
                                 else if($main-set/@relation) then $main-set/@relation
                                 else 'misc', 
                                 '{',
                                 @xml:id,
                                 ',
')

xsl:variable.select:
if(string-length($override) gt 0 and @numeration) 
                          then tr:list-number-to-integer($override, @numeration) - 1
                          else 0

xsl:value-of.select:
string-join(('[{',
                                      for $i in (1 to max((count($indizes), count($separators))))
                                      return if(    count($separators) gt count($indizes))
                                             then ($separators[$i], $list-type[$indizes[$i]])
                                             else ($list-type, $separators[$i]),
                                      '}]'         
                                      ), '')
